import React from "react";
import { fromLonLat, get } from "ol/proj";
//import { Map, GeoJSON } from "react-leaflet";

//import "leaflet/dist/leaflet.css";
//import "./SmokerMap.css";
import Map from "ol/Map";
import Feature from "ol/Feature";
import View from "ol/View";
import { Point, Style, Circle, Fill } from "ol";
import TileLayer from "ol/layer/Tile";
import VectorLayer from "ol/layer/Vector";
import VectorSource from "ol/source/Vector";
import OSM from "ol/source/OSM";


export default class SmokerMap extends React.Component{
  constructor(props){
    super(props);
    this.mapRef=React.createRef();
  }
  componentDidMount(){
    this.map=new Map({
      target: "mapContainer",
      layers: [
        new TileLayer({
          source: new OSM()
        })
      ],
      view :new View({
        center:fromLonLat([-94.6859,46.7296]),
        zoom: 6
      })

    });
  }
  //componentDidUpdate()

  componentDidUpdate() {
    if (this.props.positions) {
      this.geo_points = this.props.positions.map(
        (p) =>
          new Feature({
            geometry: new Point(fromLonLat(p))
          })
      );

      this.vectorSource = new VectorSource({
        features: this.geo_points
      });

      this.vectorLayer = new VectorLayer({
        source: this.vectorSource,
        style: new Style({
          image: new Circle({
            radius: 6,
            fill: new Fill({ color: "red" })
          })
        })
      });

      this.map.addLayer(this.vectorLayer);
    }
  }
  render() {
    console.log("-> render App");
    return (
      <div
        id="mapContainer"
        ref={this.mapRef}
        style={{ width: "80vw", height: "100vh" }}
      >
        {" "}
      </div>
    );
  }


}
