import React from "react";
import { fromLonLat, get } from "ol/proj";
//import "./SmokerMap.css";
import Map from "ol/Map";
//import Feature from "ol/Feature";
import View from "ol/View";
//import { Point, Style, Circle, Fill, Feature } from "ol";
import TileLayer from "ol/layer/Tile";
//import TileWMS from 'ol/source/TileWMS';
import VectorLayer from "ol/layer/Vector";
import VectorSource from "ol/source/Vector";
import GeoJSON from 'ol/format/GeoJSON';
import OSM from "ol/source/OSM";
import {Fill, Stroke, Style} from 'ol/style';
import {metadata} from './metadata.js';
import countyData from './countyline.geojson'


export default class SmokerMap extends React.Component{
  constructor(props){
    super(props);
    this.state = {

      smokerSource : null,
      smokerlayer: false,
      countyOutlineSource: null,
      countyOutline: false,
      countyReady: false

  };
    //console.log(this.props);
  }


  componentDidMount(){

    var basemap =new TileLayer({
      source: new OSM()
    })


    // use usestate to update the url base on 
    var smokerSource = new VectorSource({
      url:metadata[1]['geojson_url'],

      format: new GeoJSON()
    })


    var countyOutlineSource = new VectorSource({
      url: countyData,
      format: new GeoJSON()

    })
    var countyBorder = new Style({
      stroke: new Stroke({
          color: 'black',
          width: 1
      })
  });
    var countyOutline = new VectorLayer({
      opacity: 1,
      //visible: this.props.viewed === 'County',
      source: countyOutlineSource,
      style: countyBorder
    })

    var stylefunction = function(feature){
        var style;

        var value=feature.get('brfss_smoker');
        //var county=feature.get('county');

        // assign color to each break from metadata.js
        //console.log(county,value)
        for (let i =0;i<metadata[1]['break'].length;i++){
          if(value>metadata[1]['break'][i]){
          style= new Style({
            fill: new Fill({
              color: metadata[1]['color'][i]
            }),
            stroke: new Stroke({
              color:'black',
              width:0.3
            })
          })
        };
      }

        return style;

    };

    var smokerlayer = new VectorLayer({

       source: smokerSource,
       style: stylefunction,
       //showLegend:true

       })

    var olmap=new Map({
      layers: [basemap,countyOutline,smokerlayer],
      target: "mapContainer",
      view :new View({
        center:fromLonLat([-94.6859,46.7296]),
        zoom: 6
      })



    });

    this.setState({

      olmap: olmap,
      countyOutlineSource:countyOutlineSource,
      countyOutline: countyOutline,
      smokerlayer: smokerlayer,
      smokerSource:smokerSource
  })

  // when the geographic county data is loaded in add it to state of react object
  countyOutlineSource.on('change', (evt) => {
    var countyOutlineSource=evt.target;
    if (countyOutlineSource.getState() === 'ready'){
        // note that brackets are needed for dynamic names
        this.setState({countyData: countyOutlineSource.getFeatures()});
    }
})




smokerlayer.on("postrender", (event) => {
  this.setState({countyReady: true})
});



setLayerVisibility = viewed => {

  this.state.countyOutline.setVisible(viewed === 'County')
  this.state.smokerlayer.setVisible(viewed === 'County')
}

checkAndLoad = (prevProps, name) => {
  if (this.isReady(prevProps, name)) {
      // we don't want to add the cases again (except for on a new date) so set the loaded to true
      this.setState({[name + 'Loaded']: true})
      this.addField(this.props[name + 'Data'], name + "Source", this.props.fieldName)
  }
}
componentDidUpdate = (prevProps, prevState) => {
  if (prevProps.viewed !== this.props.viewed) {
      this.setLayerVisibility(this.props.viewed)
  }
  var checkAndLoad = (name) =>  this.checkAndLoad(prevProps, name)

  checkAndLoad('county')
}


  }


  render() {
    console.log("-> render App");
    return (


      <div id="mapContainer" style={{ width: "100%",height: "500px"}}></div>



    );
  }


}
